// 課題5-1 の関数 printDom() はここに記述すること
function printDom(data) {
  let old = document.querySelector('#result');
  if (old !== null) {
    old.remove();
  }
let weather = data.weather[0].description;

let message = "";
let imageurl = "";
switch (true) {
  case /晴天/.test(weather):
    message = "日焼け止めを塗ったほうがいいでしょう。";
    imageurl = "data:image/png;base64,";  
    break;

  case /雲/.test(weather):
    message = "曇りですね。穏やかな日になるでしょう。";
    imageurl = "data:image/png;base64,"; 
    break;
  case /曇/.test(weather):
    message = "曇りですね。穏やかな日になるでしょう。";
    imageurl = "data:image/png;base64,"; 
    break;

  case /雨/.test(weather):
    message = "傘を忘れずに！";
    imageurl = "data:image/png;base64,";  
    break;
  case /霧/.test(weather):
    message = "車を運転する際はお気をつけて!";
    imageurl = "data:image/jpeg;base64,";  
    break;


  case /雪/.test(weather):
    message = "手袋とカイロも忘れずに!";
    imageurl = "data:image/jpeg;base64,"; 
    break;

  default:
    imageurl = "data:image/png;base64,";
    message = "画像に対応していない天気です!";  
}


  let div = document.createElement('div');
  div.id = 'result';

  let h2 = document.createElement('h2');
  h2.textContent = "都市名: " + data.name;
  div.insertAdjacentElement('beforeend', h2);

  let ul = document.createElement('ul');

  let items = [
    "天気: " + data.weather[0].description,
    "最低気温: " + data.main.temp_min + " ℃",
    "最高気温: " + data.main.temp_max + " ℃",
    "湿度: " + data.main.humidity + " %",
    "風速: " + data.wind.speed + " m/s",
    "風向: " + data.wind.deg,
    "緯度: " + data.coord.lat + "°",
    "経度: " + data.coord.lon + "°"
  ];

 
  for (let text of items) {
    let li = document.createElement('li');
    li.textContent = text;
    ul.insertAdjacentElement('beforeend', li);
  }

  div.insertAdjacentElement('beforeend', ul);

  document.body.insertAdjacentElement('beforeend', div);
  document.querySelector('#tips').textContent = message;
  let photo = document.querySelector('#photo');
  photo.src = imageurl;

}


// 課題6-1 のイベントハンドラ登録処理は以下に記述
let dom = document.querySelector('#searchBtn');
dom.addEventListener('click', sendRequest);


// 課題6-1 のイベントハンドラ sendRequest() の定義
function sendRequest() {
  const cityId = document.querySelector('#citySelect').value;
  const url = `https://www.nishita-lab.org/web-contents/jsons/openweather/${cityId}.json`;

  axios.get(url)
    .then(showResult)
    .catch(showError)
    .then(finish); 
}


// 課題6-1: 通信が成功した時の処理は以下に記述
function showResult(resp) {
  const data = resp.data;
  printDom(data);
}


// 課題6-1: 通信エラーが発生した時の処理
function showError(err) {
    console.log(err);
}

// 課題6-1: 通信の最後にいつも実行する処理
function finish() {
    console.log('Ajax 通信が終わりました');
}

